/** @format */

import React from "react";
import "./App.css";
import Navbar from "./components/navbar";
import fetchData from "./components/api";
import Loading from "./components/loading";
import Grid from "./components/repo-grid";

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      repos: [],
      active: "all",
      loading: true
    };

    this.getData = this.getData.bind(this);
    this.handleActiveLanguage = this.handleActiveLanguage.bind(this);
  }
  componentDidMount() {
    this.getData(this.state.active);
  }

  componentDidUpdate(prevProps, prevState) {
    if (prevState.repos !== this.state.repos) {
      this.getData(this.state.active);
    }
  }

  getData(lang) {
    this.setState({
      loading: true
    });

    fetchData(lang).then(data => {
      this.setState({
        repos: data,
        loading: false
      });
    });
    console.log(data);
  }

  handleActiveLanguage(language) {
    this.setState({
      active: language
    });
  }

  render() {
    return (
      <div>
        <Loading />
        <Navbar getLanguage={this.handleActiveLanguage} />
        <h2> {this.state.active}</h2>
        <Grid repos={this.state.repos} />
      </div>
    );
  }
}
export default App;
